// Generated by CoffeeScript 1.7.1
$.fn.message = function(text) {
  var $message, date;
  $('#message').remove();
  date = new Date();
  $message = $('<div id="message"><span class="timestamp">' + date.toLocaleString() + '</span>' + text + '</div>');
  return $message.insertBefore($(this)).hide().slideDown();
};
